import React, { useState, useEffect } from 'react';

function GoalForm({ onAddGoal, categories = [] }) {
  const [formData, setFormData] = useState({
    name: '',
    targetAmount: '',
    savedAmount: 0,
    category: '',
    deadline: '',
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Set default deadline to 30 days from now when component mounts
  useEffect(() => {
    const defaultDeadline = new Date();
    defaultDeadline.setDate(defaultDeadline.getDate() + 30);
    setFormData(prev => ({
      ...prev,
      deadline: defaultDeadline.toISOString().split('T')[0]
    }));
  }, []);

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.targetAmount || formData.targetAmount <= 0) newErrors.targetAmount = 'Target amount must be greater than 0';
    if (formData.savedAmount < 0) newErrors.savedAmount = 'Saved amount cannot be negative';
    if (!formData.category.trim()) newErrors.category = 'Category is required';
    if (!formData.deadline) newErrors.deadline = 'Deadline is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'targetAmount' || name === 'savedAmount' ? parseFloat(value) || 0 : value
    });

    // Clear error for this field when user types
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: null
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    if (validateForm()) {
      // Create a new goal object with the form data
      const newGoal = {
        ...formData,
        targetAmount: parseFloat(formData.targetAmount),
        savedAmount: parseFloat(formData.savedAmount),
        createdAt: new Date().toISOString().split('T')[0]
      };

      // Call the onAddGoal function passed from the parent component
      onAddGoal(newGoal);

      // Reset the form
      setFormData({
        name: '',
        targetAmount: '',
        savedAmount: 0,
        category: '',
        deadline: formData.deadline, // Keep the deadline
      });
      setErrors({});
    }
    setIsSubmitting(false);
  };

  return (
    <div className="card">
      <div className="card-header bg-success text-white">
        <h3 className="mb-0"><i className="bi bi-plus-circle"></i> Add New Goal</h3>
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Goal Name</label>
            <input
              type="text"
              className={`form-control ${errors.name ? 'is-invalid' : ''}`}
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="e.g., Travel Fund"
            />
            {errors.name && <div className="invalid-feedback">{errors.name}</div>}
          </div>

          <div className="mb-3">
            <label htmlFor="targetAmount" className="form-label">Target Amount ($)</label>
            <input
              type="number"
              className={`form-control ${errors.targetAmount ? 'is-invalid' : ''}`}
              id="targetAmount"
              name="targetAmount"
              value={formData.targetAmount}
              onChange={handleChange}
              placeholder="e.g., 5000"
              min="0"
              step="0.01"
            />
            {errors.targetAmount && <div className="invalid-feedback">{errors.targetAmount}</div>}
          </div>

          <div className="mb-3">
            <label htmlFor="savedAmount" className="form-label">Initial Saved Amount ($)</label>
            <input
              type="number"
              className={`form-control ${errors.savedAmount ? 'is-invalid' : ''}`}
              id="savedAmount"
              name="savedAmount"
              value={formData.savedAmount}
              onChange={handleChange}
              placeholder="e.g., 0"
              min="0"
              step="0.01"
            />
            {errors.savedAmount && <div className="invalid-feedback">{errors.savedAmount}</div>}
          </div>

          <div className="mb-3">
            <label htmlFor="category" className="form-label">Category</label>
            {categories.length > 0 ? (
              <select
                className={`form-select ${errors.category ? 'is-invalid' : ''}`}
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
              >
                <option value="">Select a category...</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            ) : (
              <input
                type="text"
                className={`form-control ${errors.category ? 'is-invalid' : ''}`}
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                placeholder="e.g., Travel, Emergency, Education"
              />
            )}
            {errors.category && <div className="invalid-feedback">{errors.category}</div>}
          </div>

          <div className="mb-3">
            <label htmlFor="deadline" className="form-label">Deadline</label>
            <input
              type="date"
              className="form-control"
              id="deadline"
              name="deadline"
              value={formData.deadline}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="btn btn-success w-100 mt-3"><i className="bi bi-plus-lg"></i> Add Goal</button>
        </form>
      </div>
    </div>
  );
}

export default GoalForm;
